import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';

import usFlag from '../../img/us.png';
import phpFlag from '../../img/ph.png';
import thbFlag from '../../img/th.png';
import jpyFlag from '../../img/jp.png';

const currencyRates = {
    USD: 1, // Base currency
    PHP: 50, // Example rate, you should replace it with real-time rate
    THB: 33, // Example rate, you should replace it with real-time rate
    JPY: 110 // Example rate, you should replace it with real-time rate
};

const flagMap = {
    USD: usFlag,
    PHP: phpFlag,
    THB: thbFlag,
    JPY: jpyFlag,
};

const Multicurrency = () => {
    const [amount, setAmount] = useState('');
    const [fromCurrency, setFromCurrency] = useState('USD');
    const [toCurrency, setToCurrency] = useState('PHP');
    const [convertedAmount, setConvertedAmount] = useState('');

    const handleAmountChange = (e) => {
        setAmount(e.target.value);
    };

    const handleFromCurrencyChange = (e) => {
        setFromCurrency(e.target.value);
    };

    const handleToCurrencyChange = (e) => {
        setToCurrency(e.target.value);
    };

    const convertCurrency = () => {
        if (amount && fromCurrency && toCurrency) {
            const converted = (amount * currencyRates[toCurrency]) / currencyRates[fromCurrency];
            setConvertedAmount(converted.toFixed(2));
        }
    };

    useEffect(() => {
        convertCurrency();
    }, [amount, fromCurrency, toCurrency]);

    return (
        <PageContainer>
            <MulticurrencyContainer>
                <MulticurrencyStyled>
                    <CurrencyInput>
                        <label>Amount:</label>
                        <div className="currency-group">
                            <img src={flagMap[fromCurrency]} alt={`${fromCurrency} flag`} />
                            <select value={fromCurrency} onChange={handleFromCurrencyChange}>
                                <option value="USD">USD</option>
                                <option value="PHP">PHP</option>
                                <option value="THB">THB</option>
                                <option value="JPY">JPY</option>
                            </select>
                            <input
                                type="number"
                                value={amount}
                                onChange={handleAmountChange}
                                placeholder="Enter amount"
                            />
                        </div>
                    </CurrencyInput>
                    <Divider>
                        <SwitchIcon>â‡…</SwitchIcon>
                    </Divider>
                    <CurrencyInput>
                        <label>Converted Amount:</label>
                        <div className="currency-group">
                            <img src={flagMap[toCurrency]} alt={`${toCurrency} flag`} />
                            <select value={toCurrency} onChange={handleToCurrencyChange}>
                                <option value="USD">USD</option>
                                <option value="PHP">PHP</option>
                                <option value="THB">THB</option>
                                <option value="JPY">JPY</option>
                            </select>
                            <input
                                type="text"
                                value={convertedAmount}
                                readOnly
                            />
                        </div>
                    </CurrencyInput>
                </MulticurrencyStyled>
            </MulticurrencyContainer>
        </PageContainer>
    );
};

const PageContainer = styled.div`
    background-color: #FFFFFF;
    min-height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const MulticurrencyContainer = styled.div`
    background: #FFFFFF;
    padding: 2rem;
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const MulticurrencyStyled = styled.div`
    background: #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.1);
    padding: 3rem;
    border-radius: 20px;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    width: 100%;
    max-width: 500px;  /* Adjusted max-width */
    margin: 2rem 0;  /* Added margin */

    .converter {
        display: flex;
        flex-direction: column;
        align-items: center;
    }
`;

const CurrencyInput = styled.div`
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    margin: 1rem 0;
    width: 100%;

    label {
        margin-bottom: 0.5rem;
        font-size: 0.9rem;
        color: #7D7D7D;
    }

    .currency-group {
        display: flex;
        align-items: center;
        width: 100%;

        img {
            width: 30px;
            height: 20px;
            margin-right: 10px;
        }

        select {
            padding: 0.5rem;
            border: none;
            background: none;
            font-size: 1rem;
            margin-right: 10px;
            flex-grow: 1;
        }

        input {
            padding: 0.5rem;
            border: none;
            background-color: #f3f3f3;
            border-radius: 5px;
            width: 100%;
            max-width: 200px;
            text-align: right;
            font-size: 1rem;
            margin-left: 10px;
        }

        input[readonly] {
            background-color: #f3f3f3;
        }
    }
`;

const Divider = styled.div`
    position: relative;
    width: 100%;
    height: 50px;
    display: flex;
    align-items: center;
    justify-content: center;

    &::before {
        content: '';
        position: absolute;
        top: 50%;
        left: 0;
        right: 0;
        height: 1px;
        background: #ccc;
    }
`;

const SwitchIcon = styled.div`
    font-size: 1.5rem;
    color: white;
    display: flex;
    align-items: center;
    justify-content: center;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background-color: #222260;
    position: relative;
    z-index: 1;
`;

export default Multicurrency;
