import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";

const IncomeForm = ({ incomeData, onSubmit }) => {
    const [inputState, setInputState] = useState({
        title: '',
        amount: '',
        date: '',
        category: '',
        description: '',
    });

    useEffect(() => {
        if (incomeData) {
            setInputState({
                title: incomeData.title,
                amount: incomeData.amount,
                date: new Date(incomeData.date),
                category: incomeData.category,
                description: incomeData.description,
            });
        }
    }, [incomeData]);

    const { title, amount, date, category, description } = inputState;

    const handleInput = name => e => {
        setInputState({ ...inputState, [name]: e.target.value });
    };

    const handleSubmit = e => {
        e.preventDefault();
        onSubmit(inputState);
        setInputState({
            title: '',
            amount: '',
            date: '',
            category: '',
            description: '',
        });
    };

    return (
        <FormStyled onSubmit={handleSubmit}>
            <h3>{incomeData ? 'Update Income' : 'Add Income'}</h3>
            <label>Title</label>
            <input type="text" value={title} onChange={handleInput('title')} />
            <label>Amount</label>
            <input type="number" value={amount} onChange={handleInput('amount')} />
            <label>Date</label>
            <DatePicker selected={date} onChange={(date) => setInputState({ ...inputState, date })} />
            <label>Category</label>
            <select value={category} onChange={handleInput('category')}>
                <option value="" disabled>Select Category</option>
                <option value="salary">Salary</option>
                <option value="freelancing">Freelancing</option>
                <option value="investments">Investments</option>
                <option value="stocks">Stocks</option>
                <option value="bitcoin">Bitcoin</option>
                <option value="bank">Bank Transfer</option>
                <option value="youtube">YouTube</option>
                <option value="other">Other</option>
            </select>
            <label>Description</label>
            <textarea value={description} onChange={handleInput('description')} />
            <button type="submit">{incomeData ? 'Update' : 'Add'}</button>
        </FormStyled>
    );
};

const FormStyled = styled.form`
    display: flex;
    flex-direction: column;
    gap: 1rem;
    label {
        font-weight: bold;
    }
    input, select, textarea {
        padding: 0.5rem;
        border: 1px solid #ccc;
        border-radius: 5px;
    }
    button {
        padding: 0.5rem;
        background: #007bff;
        color: white;
        border: none;
        border-radius: 5px;
        cursor: pointer;
    }
`;

export default IncomeForm;
