import React, { useState, useEffect } from 'react';
import { Chart as ChartJs, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, ArcElement, BarElement } from 'chart.js';
import { Line, Pie, Bar } from 'react-chartjs-2';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import { parseISO, startOfMonth, endOfMonth, startOfWeek, endOfWeek, startOfYear, endOfYear, isWithinInterval, format } from 'date-fns';

ChartJs.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
    BarElement
);

function Chart() {
    const { incomes, expenses } = useGlobalContext();
    const [timeframe, setTimeframe] = useState('monthly');

    const now = new Date();

    const filterItemsByDateRange = (items, startDate, endDate) => {
        return items.filter(item => {
            const itemDate = parseISO(item.date);
            return isWithinInterval(itemDate, { start: startDate, end: endDate });
        });
    };

    const aggregateData = (items, period) => {
        const periods = {
            monthly: { start: startOfMonth(now), end: endOfMonth(now) },
            weekly: { start: startOfWeek(now), end: endOfWeek(now) },
            yearly: { start: startOfYear(now), end: endOfYear(now) },
        };

        const { start, end } = periods[period];
        const filteredItems = filterItemsByDateRange(items, start, end);

        return filteredItems.map(item => ({
            ...item,
            formattedDate: format(parseISO(item.date), 'dd/MM/yyyy')
        }));
    };

    const getAggregatedData = () => {
        const aggregatedIncomes = aggregateData(incomes, timeframe);
        const aggregatedExpenses = aggregateData(expenses, timeframe);

        return {
            aggregatedIncomes,
            aggregatedExpenses,
            lineData: {
                labels: aggregatedIncomes.map(inc => inc.formattedDate),
                datasets: [
                    {
                        label: 'Income',
                        data: aggregatedIncomes.map(income => income.amount),
                        borderColor: 'green',
                        backgroundColor: 'rgba(0, 255, 0, 0.5)',
                        tension: 0.2,
                        fill: true,
                    },
                    {
                        label: 'Expenses',
                        data: aggregatedExpenses.map(expense => expense.amount),
                        borderColor: 'red',
                        backgroundColor: 'rgba(255, 0, 0, 0.5)',
                        tension: 0.2,
                        fill: true,
                    }
                ]
            },
            pieData: {
                labels: ['Income', 'Expenses'],
                datasets: [
                    {
                        data: [aggregatedIncomes.reduce((acc, inc) => acc + inc.amount, 0), aggregatedExpenses.reduce((acc, exp) => acc + exp.amount, 0)],
                        backgroundColor: ['green', 'red'],
                    },
                ],
            },
            barData: {
                labels: aggregatedIncomes.map(inc => inc.formattedDate),
                datasets: [
                    {
                        label: 'Income',
                        data: aggregatedIncomes.map(income => income.amount),
                        backgroundColor: 'green',
                    },
                    {
                        label: 'Expenses',
                        data: aggregatedExpenses.map(expense => expense.amount),
                        backgroundColor: 'red',
                    },
                ],
            }
        };
    };

    const { aggregatedIncomes, aggregatedExpenses, lineData, pieData, barData } = getAggregatedData();

    const barOptions = {
        scales: {
            x: {
                stacked: true,
            },
            y: {
                stacked: true,
            },
        },
    };

    const lineOptions = {
        responsive: true,
        plugins: {
            legend: {
                position: 'bottom',
                labels: {
                    font: {
                        family: 'Segoe UI',
                    }
                }
            },
            tooltip: {
                mode: 'index',
                intersect: false,
                bodyFont: {
                    family: 'Segoe UI',
                },
                titleFont: {
                    family: 'Segoe UI',
                },
            },
        },
        scales: {
            x: {
                display: true,
                title: {
                    display: false,
                },
                ticks: {
                    font: {
                        family: 'Segoe UI',
                    }
                }
            },
            y: {
                display: true,
                title: {
                    display: false,
                },
                beginAtZero: true,
                ticks: {
                    font: {
                        family: 'Segoe UI',
                    }
                }
            }
        }
    };

    return (
        <ChartStyled>
            <div className="header">
                <div className="timeframe-selector">
                    <label htmlFor="timeframe">Select Timeframe: </label>
                    <select id="timeframe" value={timeframe} onChange={(e) => setTimeframe(e.target.value)}>
                        <option value="weekly">Weekly</option>
                        <option value="monthly">Monthly</option>
                        <option value="yearly">Yearly</option>
                    </select>
                </div>
            </div>
            <Line options={lineOptions} data={lineData} />
            <ChartsContainer>
                <PieChartStyled>
                    <div className="chart-box">
                        <h3>Expenses by Category</h3>
                        <Pie data={pieData} />
                    </div>
                </PieChartStyled>
                <BarChartStyled>
                    <div className="chart-box">
                        <h3>Expenses by Month</h3>
                        <Bar data={barData} options={barOptions} />
                    </div>
                </BarChartStyled>
            </ChartsContainer>
        </ChartStyled>
    );
}

const ChartStyled = styled.div`
    background: #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0);
    padding: 1rem;
    border-radius: 20px;
    height: 100%;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;

    .header {
        display: flex;
        justify-content: flex-end;
    }

    .timeframe-selector {
        display: flex;
        align-items: center;
        margin-bottom: 0.5rem;

        label {
            margin-right: 1rem;
            font-size: 1rem;
            color: #696969;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
        }

        select {
            padding: 0.5rem;
            border: 1px solid #ddd;
            border-radius: 5px;
            font-size: 1rem;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            color: #696969;
        }
    }
`;

const ChartsContainer = styled.div`
    display: flex;
    justify-content: space-between;
    margin-top: 2rem;
`;

const PieChartStyled = styled.div`
    background: #FFFFFF;
    padding: 1rem;
    height: 100%;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    display: flex;
    flex-direction: row;
    align-items: center;

    .chart-box {
        padding: 1rem;
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        height: 300px;
    }
`;

const BarChartStyled = styled.div`
    background: #FFFFFF;
    padding: 1rem;
    height: 100%;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;

    .chart-box {
        padding: 1rem;
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        height: 300px;
    }
`;

export default Chart;
