import React from 'react';
import { Chart as ChartJs, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, ArcElement, BarElement } from 'chart.js';
import { Line, Pie, Bar } from 'react-chartjs-2';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import { parseISO, format } from 'date-fns'; // Only import what's needed

ChartJs.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
    BarElement
);

function Chart() {
    const { incomes, expenses } = useGlobalContext();

    // Aggregate all data, no date range filtering
    const aggregateData = (items) => items.map(item => ({
        ...item,
        formattedDate: format(parseISO(item.date), 'dd/MM/yyyy')
    }));

    const getAggregatedData = () => {
        const aggregatedIncomes = aggregateData(incomes);
        const aggregatedExpenses = aggregateData(expenses);

        const allItems = [...aggregatedIncomes, ...aggregatedExpenses].sort((a, b) => new Date(a.date) - new Date(b.date)); // Ensure sorted by date

        return {
            lineData: {
                labels: allItems.map(item => item.formattedDate),
                datasets: [
                    {
                        label: 'Incomes',
                        data: allItems.filter(item => incomes.includes(item)).map(item => item.amount),
                        borderColor: 'green',
                        backgroundColor: 'rgba(0, 255, 0, 0.5)',
                        tension: 0.2,
                        fill: true,
                    },
                    {
                        label: 'Expenses',
                        data: allItems.filter(item => expenses.includes(item)).map(item => item.amount),
                        borderColor: 'red',
                        backgroundColor: 'rgba(255, 0, 0, 0.5)',
                        tension: 0.2,
                        fill: true,
                    }
                ]
            },
            pieData: {
                labels: ['Income', 'Expenses'],
                datasets: [
                    {
                        data: [
                            incomes.reduce((acc, item) => acc + item.amount, 0),
                            expenses.reduce((acc, item) => acc + item.amount, 0)
                        ],
                        backgroundColor: ['green', 'red'],
                    },
                ],
            },
            barData: {
                labels: allItems.map(item => item.formattedDate),
                datasets: [
                    {
                        label: 'Income',
                        data: incomes.map(income => income.amount),
                        backgroundColor: 'green',
                    },
                    {
                        label: 'Expenses',
                        data: expenses.map(expense => expense.amount),
                        backgroundColor: 'red',
                    },
                ],
            }
        };
    };

    const { lineData, pieData, barData } = getAggregatedData();

    const lineOptions = {
        responsive: true,
        plugins: {
            legend: {
                position: 'bottom',
                labels: {
                    font: {
                        family: 'Segoe UI',
                    }
                }
            },
            tooltip: {
                mode: 'index',
                intersect: false,
                bodyFont: {
                    family: 'Segoe UI',
                },
                titleFont: {
                    family: 'Segoe UI',
                },
            },
        },
        scales: {
            x: {
                display: true,
                title: {
                    display: false,
                },
                ticks: {
                    font: {
                        family: 'Segoe UI',
                    }
                }
            },
            y: {
                display: true,
                title: {
                    display: false,
                },
                beginAtZero: true,
                ticks: {
                    font: {
                        family: 'Segoe UI',
                    }
                }
            }
        }
    };

    return (
        <ChartStyled>
            <Line options={lineOptions} data={lineData} />
            <ChartsContainer>
                <PieChartStyled>
                    <div className="chart-box">
                        <h3>Expenses by Category</h3>
                        <Pie data={pieData} />
                    </div>
                </PieChartStyled>
                <BarChartStyled>
                    <div className="chart-box">
                        <h3>Expenses by Month</h3>
                        <Bar data={barData} options={lineOptions} />
                    </div>
                </BarChartStyled>
            </ChartsContainer>
        </ChartStyled>
    );
}

const ChartStyled = styled.div`
    background: #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0);
    padding: 1rem;
    border-radius: 20px;
    height: 100%;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
`;

const ChartsContainer = styled.div`
    display: flex;
    justify-content: space-between;
    margin-top: 2rem;
`;

const PieChartStyled = styled.div`
    background: #FFFFFF;
    padding: 1rem;
    height: 100%;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    display: flex;
    flex-direction: row;
    align-items: center;

    .chart-box {
        padding: 1rem;
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        height: 300px;
    }
`;

const BarChartStyled = styled.div`
    background: #FFFFFF;
    padding: 1rem;
    height: 100%;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;

    .chart-box {
        padding: 1rem;
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        height: 300px;
    }
`;

export default Chart;
