import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';

const currencyRates = {
    USD: 1, // Base currency
    PHP: 50, // Example rate, you should replace it with real-time rate
    THB: 33, // Example rate, you should replace it with real-time rate
    JPY: 110 // Example rate, you should replace it with real-time rate
};

const Multicurrency = () => {
    const [amount, setAmount] = useState('');
    const [fromCurrency, setFromCurrency] = useState('USD');
    const [toCurrency, setToCurrency] = useState('PHP');
    const [convertedAmount, setConvertedAmount] = useState('');

    const handleAmountChange = (e) => {
        setAmount(e.target.value);
    };

    const handleFromCurrencyChange = (e) => {
        setFromCurrency(e.target.value);
    };

    const handleToCurrencyChange = (e) => {
        setToCurrency(e.target.value);
    };

    const convertCurrency = () => {
        if (amount && fromCurrency && toCurrency) {
            const converted = (amount * currencyRates[toCurrency]) / currencyRates[fromCurrency];
            setConvertedAmount(converted.toFixed(2));
        }
    };

    useEffect(() => {
        convertCurrency();
    }, [amount, fromCurrency, toCurrency]);

    return (
        <PageContainer>
            <MulticurrencyContainer>
                <MulticurrencyStyled>
                    <h2>Multi-currency Converter</h2>
                    <div className="converter">
                        <div className="input-group">
                            <label>Amount:</label>
                            <input
                                type="number"
                                value={amount}
                                onChange={handleAmountChange}
                                placeholder="Enter amount"
                            />
                        </div>
                        <div className="input-group">
                            <label>From:</label>
                            <select value={fromCurrency} onChange={handleFromCurrencyChange}>
                                <option value="USD">USD</option>
                                <option value="PHP">PHP</option>
                                <option value="THB">THB</option>
                                <option value="JPY">JPY</option>
                            </select>
                        </div>
                        <div className="input-group">
                            <label>To:</label>
                            <select value={toCurrency} onChange={handleToCurrencyChange}>
                                <option value="USD">USD</option>
                                <option value="PHP">PHP</option>
                                <option value="THB">THB</option>
                                <option value="JPY">JPY</option>
                            </select>
                        </div>
                        <div className="result">
                            <h3>Converted Amount: {convertedAmount} {toCurrency}</h3>
                        </div>
                    </div>
                </MulticurrencyStyled>
            </MulticurrencyContainer>
        </PageContainer>
    );
};

const PageContainer = styled.div`
    background-color: #FFFFFF;
    min-height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const MulticurrencyContainer = styled.div`
    background: #FFFFFF;
    padding: 2rem;
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const MulticurrencyStyled = styled.div`
    background: #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.1);
    padding: 2rem;
    border-radius: 20px;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    width: 100%;
    max-width: 600px;

    h2 {
        text-align: center;
        margin-bottom: 1rem;
    }

    .converter {
        display: flex;
        flex-direction: column;
        align-items: center;

        .input-group {
            margin: 1rem 0;
            display: flex;
            flex-direction: column;
            align-items: flex-start;

            label {
                margin-bottom: 0.5rem;
                font-weight: bold;
            }

            input, select {
                padding: 0.5rem;
                border: 1px solid #ccc;
                border-radius: 5px;
                width: 200px;
            }
        }

        .result {
            margin-top: 2rem;
            h3 {
                font-weight: bold;
                color: #009B72;
            }
        }
    }
`;

export default Multicurrency;
