import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import History from '../../History/History';
import { InnerLayout } from '../../styles/Layouts';
import { dollar } from '../../utils/Icons';
import Chart from '../Chart/Chart';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { FaCalendarAlt } from 'react-icons/fa';

function Dashboard() {
    const { totalExpenses, incomes, expenses, totalIncome, getIncomes, getExpenses } = useGlobalContext();

    useEffect(() => {
        getIncomes();
        getExpenses();
    }, []);

    const [selectedDate, setSelectedDate] = useState(new Date());
    const [filteredIncomes, setFilteredIncomes] = useState([]);
    const [filteredExpenses, setFilteredExpenses] = useState([]);

    useEffect(() => {
        const filterData = () => {
            const filteredIncomes = incomes.filter(income => 
                new Date(income.date).toLocaleDateString() === selectedDate.toLocaleDateString()
            );
            const filteredExpenses = expenses.filter(expense => 
                new Date(expense.date).toLocaleDateString() === selectedDate.toLocaleDateString()
            );
            setFilteredIncomes(filteredIncomes);
            setFilteredExpenses(filteredExpenses);
        };
        filterData();
    }, [selectedDate, incomes, expenses]);

    const resetFilters = () => {
        setSelectedDate(new Date());
        setFilteredIncomes(incomes);
        setFilteredExpenses(expenses);
    };

    return (
        <DashboardStyled>
            <InnerLayout>
                <div className="header">
                    <h2>Dashboard</h2>
                    <div className="date-picker">
                        <DatePickerWrapper>
                            <DatePicker
                                selected={selectedDate}
                                onChange={(date) => setSelectedDate(date)}
                                dateFormat="MM/dd/yyyy"
                                customInput={<CustomDatePickerButton />}
                            />
                        </DatePickerWrapper>
                        <ResetButton onClick={resetFilters}>Reset</ResetButton>
                    </div>
                </div>
                <p>{selectedDate.toDateString()}</p> 
                <div className="stats-con">
                    <div>
                        <h2>Total Balance</h2>
                        <p style={{ color: '#454545' }}>{dollar}{filteredIncomes.reduce((acc, income) => acc + income.amount, 0) - filteredExpenses.reduce((acc, expense) => acc + expense.amount, 0)}</p>
                    </div>
                    <div>
                        <h2>Total Income</h2>
                        <p style={{ color: 'green' }}>{dollar}{filteredIncomes.reduce((acc, income) => acc + income.amount, 0)}</p>
                    </div>
                    <div>
                        <h2>Total Expense</h2>
                        <p style={{ color: 'red' }}>{dollar}{filteredExpenses.reduce((acc, expense) => acc + expense.amount, 0)}</p>
                    </div>
                </div>
                <div className="chart-con">
                    <h2>Analytics</h2>
                    <small>Track your expenses and income.</small>
                    <Chart incomes={filteredIncomes} expenses={filteredExpenses} />
                </div>
                <div className="history-con">
                    <h2>Transaction History</h2>
                    <History />
                </div>
            </InnerLayout>
        </DashboardStyled>
    );
}

const CustomDatePickerButton = React.forwardRef(({ value, onClick }, ref) => (
    <DateButton onClick={onClick} ref={ref}>
        <FaCalendarAlt size={20} />
        <span>{value}</span>
    </DateButton>
));

const DashboardStyled = styled.div`
    font-family: 'Segoe UI', sans-serif;
    background-color: #ffffff;
    padding: 20px;

    .header {
        display: flex;
        justify-content: space-between;
        align-items: center;
    }

    .date-picker {
        display: flex;
        align-items: center;
        margin-bottom: 20px;
    }

    .stats-con {
        margin-top: 1rem;
        display: grid;
        grid-template-columns: repeat(3, 1fr);
        grid-gap: 1rem;
        margin-bottom: 1rem;

        > div {
            background: #ffffff;
            border-radius: 8px;
            box-shadow: 0 4px 8px rgba(0,0,0,0.08);
            padding: 20px;
            display: flex;
            flex-direction: column;
            justify-content: space-between;
            margin: 1rem 0;

            h2 {
                font-size: 1.2rem;
                color: #222260;
                margin-bottom: 0.5rem;
            }
            p {
                font-size: 2.4rem;
                font-weight: bold;
                margin: 0;
            }
            small {
                font-size: 1rem;
                color: #666;
            }
        }
    }

    .chart-con {
        background: #ffffff;
        border-radius: 8px;
        box-shadow: 0 4px 8px rgba(0,0,0,0.08);
        padding: 20px;
        margin-bottom: 2rem;
    }

    .history-con {
        background: #ffffff;
        border-radius: 8px;
        box-shadow: 0 4px 8px rgba(0,0,0,0.08);
        padding: 20px;
        display: flex;
        flex-direction: column;

        h2 {
            font-size: 1.5rem;
            margin-bottom: 1rem;
        }

        .salary-item {
            display: flex;
            justify-content: space-between;
            margin-top: 0.5rem;

            p {
                font-weight: bold;
                font-size: 1.4rem;
            }
        }
    }
`;

const DatePickerWrapper = styled.div`
    display: inline-block;
`;

const DateButton = styled.button`
    display: flex;
    align-items: center;
    background-color: #222260;
    border: 1px solid #ccc;
    border-radius: 5px;
    padding: 0.5rem 1rem;
    font-size: 1rem;
    color: white;
    cursor: pointer;
    outline: none;

    span {
        margin-left: 0.5rem;
    }

    &:hover {
        background-color: #1e1e5b;
    }
`;

const ResetButton = styled.button`
    margin-left: 10px;
    background-color: #f0f0f0;
    border: 1px solid #ccc;
    border-radius: 5px;
    padding: 0.5rem 1rem;
    font-size: 1rem;
    color: #222260;
    cursor: pointer;
    outline: none;

    &:hover {
        background-color: #e0e0e0;
    }
`;

export default Dashboard;
