import React, { useState, useEffect } from 'react';
import { Chart as ChartJs, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, ArcElement, BarElement } from 'chart.js';
import { Line, Pie, Bar } from 'react-chartjs-2';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import { parseISO, startOfMonth, endOfMonth, isWithinInterval, format } from 'date-fns'; // Removed unused imports

ChartJs.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
    BarElement
);

function Chart() {
    const { incomes, expenses } = useGlobalContext();
    const now = new Date();

    const filterItemsByDateRange = (items, startDate, endDate) => {
        return items.filter(item => {
            const itemDate = parseISO(item.date);
            return isWithinInterval(itemDate, { start: startDate, end: endDate });
        });
    };

    const aggregateData = (items) => {
        const start = startOfMonth(now);
        const end = endOfMonth(now);
        const filteredItems = filterItemsByDateRange(items, start, end);

        return filteredItems.map(item => ({
            ...item,
            formattedDate: format(parseISO(item.date), 'dd/MM/yyyy')
        }));
    };

    const getAggregatedData = () => {
        const aggregatedIncomes = aggregateData(incomes);
        const aggregatedExpenses = aggregateData(expenses);

        // Combine incomes and expenses into a single dataset for the line graph
        const combinedData = [...aggregatedIncomes, ...aggregatedExpenses];

        return {
            lineData: {
                labels: combinedData.map(item => item.formattedDate),
                datasets: [
                    {
                        label: 'All Data',
                        data: combinedData.map(item => item.amount),
                        borderColor: 'blue', // Choose a color for the line
                        backgroundColor: 'rgba(0, 0, 255, 0.5)', // Choose a color for the area under the line
                        tension: 0.2,
                        fill: true,
                    }
                ]
            },
            pieData: {
                labels: ['Income', 'Expenses'],
                datasets: [
                    {
                        data: [incomes.reduce((acc, inc) => acc + inc.amount, 0), expenses.reduce((acc, exp) => acc + exp.amount, 0)],
                        backgroundColor: ['green', 'red'],
                    },
                ],
            },
            barData: {
                labels: aggregatedIncomes.map(inc => inc.formattedDate),
                datasets: [
                    {
                        label: 'Income',
                        data: aggregatedIncomes.map(income => income.amount),
                        backgroundColor: 'green',
                    },
                    {
                        label: 'Expenses',
                        data: aggregatedExpenses.map(expense => expense.amount),
                        backgroundColor: 'red',
                    },
                ],
            }
        };
    };

    const { lineData, pieData, barData } = getAggregatedData();

    const barOptions = {
        scales: {
            x: {
                stacked: true,
            },
            y: {
                stacked: true,
            },
        },
    };

    const lineOptions = {
        responsive: true,
        plugins: {
            legend: {
                position: 'bottom',
                labels: {
                    font: {
                        family: 'Segoe UI',
                    }
                }
            },
            tooltip: {
                mode: 'index',
                intersect: false,
                bodyFont: {
                    family: 'Segoe UI',
                },
                titleFont: {
                    family: 'Segoe UI',
                },
            },
        },
        scales: {
            x: {
                display: true,
                title: {
                    display: false,
                },
                ticks: {
                    font: {
                        family: 'Segoe UI',
                    }
                }
            },
            y: {
                display: true,
                title: {
                    display: false,
                },
                beginAtZero: true,
                ticks: {
                    font: {
                        family: 'Segoe UI',
                    }
                }
            }
        }
    };

    return (
        <ChartStyled>
            <Line options={lineOptions} data={lineData} />
            <ChartsContainer>
                <PieChartStyled>
                    <div className="chart-box">
                        <h3>Expenses by Category</h3>
                        <Pie data={pieData} />
                    </div>
                </PieChartStyled>
                <BarChartStyled>
                    <div className="chart-box">
                        <h3>Expenses by Month</h3>
                        <Bar data={barData} options={barOptions} />
                    </div>
                </BarChartStyled>
            </ChartsContainer>
        </ChartStyled>
    );
}

const ChartStyled = styled.div`
    background: #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0);
    padding: 1rem;
    border-radius: 20px;
    height: 100%;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
`;

const ChartsContainer = styled.div`
    display: flex;
    justify-content: space-between;
    margin-top: 2rem;
`;

const PieChartStyled = styled.div`
    background: #FFFFFF;
    padding: 1rem;
    height: 100%;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    display: flex;
    flex-direction: row;
    align-items: center;

    .chart-box {
        padding: 1rem;
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        height: 300px;
    }
`;

const BarChartStyled = styled.div`
    background: #FFFFFF;
    padding: 1rem;
    height: 100%;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;

    .chart-box {
        padding: 1rem;
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        height: 300px;
    }
`;

export default Chart;
