import React from 'react';
import { Chart as ChartJs, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, ArcElement, BarElement } from 'chart.js';
import { Line, Pie, Bar } from 'react-chartjs-2';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import { parseISO, format } from 'date-fns';

ChartJs.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
    BarElement
);

function Chart() {
    const { incomes, expenses } = useGlobalContext();

    const aggregateData = (items) => items.map(item => ({
        ...item,
        formattedDate: format(parseISO(item.date), 'dd/MM/yyyy')
    }));

    // Sorting items by date to align in the chart
    const sortItemsByDate = (items) => items.sort((a, b) => new Date(a.date) - new Date(b.date));

    const getAggregatedData = () => {
        const allIncomes = aggregateData(incomes);
        const allExpenses = aggregateData(expenses);
        const allItems = sortItemsByDate([...allIncomes, ...allExpenses]);

        const uniqueDates = [...new Set(allItems.map(item => item.formattedDate))];

        return {
            lineData: {
                labels: uniqueDates,
                datasets: [
                    {
                        label: 'Income',
                        data: uniqueDates.map(date => {
                            const total = allIncomes.filter(item => item.formattedDate === date).reduce((acc, curr) => acc + curr.amount, 0);
                            return total;
                        }),
                        borderColor: 'green',
                        backgroundColor: 'rgba(0, 255, 0, 0.5)',
                        tension: 0.2,
                        fill: true,
                    },
                    {
                        label: 'Expenses',
                        data: uniqueDates.map(date => {
                            const total = allExpenses.filter(item => item.formattedDate === date).reduce((acc, curr) => acc + curr.amount, 0);
                            return total;
                        }),
                        borderColor: 'red',
                        backgroundColor: 'rgba(255, 0, 0, 0.5)',
                        tension: 0.2,
                        fill: true,
                    }
                ]
            }
        };
    };

    const { lineData } = getAggregatedData();

    const lineOptions = {
        responsive: true,
        plugins: {
            legend: {
                position: 'bottom',
                labels: {
                    font: {
                        family: 'Segoe UI',
                    }
                }
            },
            tooltip: {
                mode: 'index',
                intersect: false,
                bodyFont: {
                    family: 'Segoe UI',
                },
                titleFont: {
                    family: 'Segoe UI',
                },
            },
        },
        scales: {
            x: {
                display: true,
                title: {
                    display: false,
                },
                ticks: {
                    font: {
                        family: 'Segoe UI',
                    }
                }
            },
            y: {
                display: true,
                title: {
                    display: false,
                },
                beginAtZero: true,
                ticks: {
                    font: {
                        family: 'Segoe UI',
                    }
                }
            }
        }
    };

    return (
        <ChartStyled>
            <Line options={lineOptions} data={lineData} />
        </ChartStyled>
    );
}

const ChartStyled = styled.div`
    background: #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0);
    padding: 1rem;
    border-radius: 20px;
    height: 100%;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
`;

export default Chart;
