import React from 'react';
import { Chart as ChartJs, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, ArcElement, BarElement } from 'chart.js';
import { Line } from 'react-chartjs-2';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import { parseISO, format } from 'date-fns';

ChartJs.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
    BarElement
);

function Chart() {
    const { incomes, expenses } = useGlobalContext();

    const aggregateData = (items) => items.map(item => ({
        ...item,
        formattedDate: format(parseISO(item.date), 'dd/MM/yyyy')
    }));

    const sortItemsByDate = (items) => items.sort((a, b) => new Date(a.date) - new Date(b.date));

    const getAggregatedData = () => {
        const allIncomes = aggregateData(incomes);
        const allExpenses = aggregateData(expenses);

        const incomeDates = allIncomes.map(item => item.formattedDate);
        const expenseDates = allExpenses.map(item => item.formattedDate);
        let uniqueDates = [...new Set([...incomeDates, ...expenseDates])];

        uniqueDates = uniqueDates.sort((a, b) => new Date(a.split('/').reverse().join('-')) - new Date(b.split('/').reverse().join('-')));

        const incomeData = allIncomes.map(inc => ({
            date: inc.formattedDate,
            amount: inc.amount
        }));

        const expenseData = allExpenses.map(exp => ({
            date: exp.formattedDate,
            amount: exp.amount
        }));

        return {
            lineData: {
                labels: uniqueDates,
                datasets: [
                    {
                        label: 'Income',
                        data: uniqueDates.map(date => {
                            const item = incomeData.find(item => item.date === date);
                            return item ? item.amount : undefined; // Ensure only non-zero values are plotted
                        }),
                        borderColor: 'green',
                        backgroundColor: 'rgba(0, 255, 0, 0.5)',
                        tension: 0.2,
                        fill: true,
                    },
                    {
                        label: 'Expenses',
                        data: uniqueDates.map(date => {
                            const item = expenseData.find(item => item.date === date);
                            return item ? item.amount : undefined; 
                        }),
                        borderColor: 'red',
                        backgroundColor: 'rgba(255, 0, 0, 0.5)',
                        tension: 0.2,
                        fill: true,
                    }
                ]
            }
        };
    };

    const { lineData } = getAggregatedData();

    const lineOptions = {
        responsive: true,
        plugins: {
            legend: {
                position: 'bottom',
                labels: {
                    font: {
                        family: 'Segoe UI',
                    }
                }
            },
            tooltip: {
                mode: 'index',
                intersect: false,
                bodyFont: {
                    family: 'Segoe UI',
                },
                titleFont: {
                    family: 'Segoe UI',
                },
            },
        },
        scales: {
            x: {
                display: true,
                title: {
                    display: false,
                },
                ticks: {
                    font: {
                        family: 'Segoe UI',
                    }
                }
            },
            y: {
                display: true,
                title: {
                    display: false,
                },
                beginAtZero: true,
                ticks: {
                    font: {
                        family: 'Segoe UI',
                    }
                }
            }
        }
    };

    return (
        <ChartStyled>
            <div className="chart-con">
                <h2>Analytics!</h2>
                    <small>Track your expenses and income. </small>
            </div>
            <Line options={lineOptions} data={lineData} />
        </ChartStyled>
    );
}

const ChartStyled = styled.div`
    background: #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0);
    padding: 1rem;
    border-radius: 20px;
    height: 100%;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;

    .chart-con {
        background: #ffffff;
        border-radius: 8px;
        box-shadow: 0 4px 8px rgba(0,0,0,0.08);
        padding: 20px;
        margin-bottom: 2rem;
    }
`;

export default Chart;
