import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";

const ExpenseForm = ({ expenseData, onSubmit }) => {
    const [inputState, setInputState] = useState({
        title: '',
        amount: '',
        date: '',
        category: '',
        description: '',
    });

    useEffect(() => {
        if (expenseData) {
            setInputState({
                title: expenseData.title,
                amount: expenseData.amount,
                date: new Date(expenseData.date),
                category: expenseData.category,
                description: expenseData.description,
            });
        }
    }, [expenseData]);

    const { title, amount, date, category, description } = inputState;

    const handleInput = name => e => {
        setInputState({ ...inputState, [name]: e.target.value });
    };

    const handleSubmit = e => {
        e.preventDefault();
        onSubmit(inputState);
        setInputState({
            title: '',
            amount: '',
            date: '',
            category: '',
            description: '',
        });
    };

    return (
        <FormStyled onSubmit={handleSubmit}>
            <h3>{expenseData ? 'Update Expense' : 'Add Expense'}</h3>
            <label>Title</label>
            <input type="text" value={title} onChange={handleInput('title')} />
            <label>Amount</label>
            <input type="number" value={amount} onChange={handleInput('amount')} />
            <label>Date</label>
            <DatePicker selected={date} onChange={(date) => setInputState({ ...inputState, date })} />
            <label>Category</label>
            <select value={category} onChange={handleInput('category')}>
                <option value="" disabled>Select Category</option>
                <option value="education">Education</option>
                <option value="groceries">Groceries</option>
                <option value="health">Health</option>
                <option value="subscriptions">Subscriptions</option>
                <option value="takeaways">Takeaways</option>
                <option value="clothing">Clothing</option>
                <option value="travelling">Travelling</option>
                <option value="other">Other</option>
            </select>
            <label>Description</label>
            <textarea value={description} onChange={handleInput('description')} />
            <button type="submit">{expenseData ? 'Update' : 'Add'}</button>
        </FormStyled>
    );
};

const FormStyled = styled.form`
    display: flex;
    flex-direction: column;
    gap: 1rem;
    label {
        font-weight: bold;
    }
    input, select, textarea {
        padding: 0.5rem;
        border: 1px solid #ccc;
        border-radius: 5px;
    }
    button {
        padding: 0.5rem;
        background: #007bff;
        color: white;
        border: none;
        border-radius: 5px;
        cursor: pointer;
    }
`;

export default ExpenseForm;
