import React, { useState } from 'react';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import axios from 'axios';

function Settings() {
    const { currentUser, setCurrentUser } = useGlobalContext();
    const [name, setName] = useState(currentUser.name);
    const [password, setPassword] = useState('');
    const [profilePicture, setProfilePicture] = useState(null);

    const handleNameChange = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.put('/settings', { name }, {
                headers: { sessionToken: currentUser.authentication.sessionToken }
            });
            setCurrentUser(response.data.user);
            alert('Name updated successfully');
        } catch (error) {
            console.error(error);
            alert('Failed to update name');
        }
    };

    const handlePasswordChange = async (e) => {
        e.preventDefault();
        try {
            await axios.put('/settings', { password }, {
                headers: { sessionToken: currentUser.authentication.sessionToken }
            });
            alert('Password updated successfully');
        } catch (error) {
            console.error(error);
            alert('Failed to update password');
        }
    };

    const handleProfilePictureChange = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('profilePicture', profilePicture);
        try {
            const response = await axios.put('/settings/profile-picture', formData, {
                headers: {
                    sessionToken: currentUser.authentication.sessionToken,
                    'Content-Type': 'multipart/form-data'
                }
            });
            setCurrentUser(response.data.user);
            alert('Profile picture updated successfully');
        } catch (error) {
            console.error(error);
            alert('Failed to update profile picture');
        }
    };

    return (
        <SettingsStyled>
            <h2>Settings</h2>
            <form onSubmit={handleNameChange}>
                <label>
                    Name:
                    <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
                </label>
                <button type="submit">Update Name</button>
            </form>
            <form onSubmit={handlePasswordChange}>
                <label>
                    Password:
                    <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
                </label>
                <button type="submit">Update Password</button>
            </form>
            <form onSubmit={handleProfilePictureChange} encType="multipart/form-data">
                <label>
                    Profile Picture:
                    <input type="file" onChange={(e) => setProfilePicture(e.target.files[0])} />
                </label>
                <button type="submit">Update Profile Picture</button>
            </form>
        </SettingsStyled>
    );
}

const SettingsStyled = styled.div`
    padding: 2rem;
    background-color: #ffffff;
    border-radius: 8px;
    h2 {
        margin-bottom: 2rem;
        font-family: 'Segoe UI', sans-serif;
    }
    form {
        display: flex;
        flex-direction: column;
        margin-bottom: 2rem;
        label {
            margin-bottom: 1rem;
            font-family: 'Segoe UI', sans-serif;
            input {
                margin-left: 1rem;
                padding: 0.5rem;
                border: 1px solid #ccc;
                border-radius: 5px;
                font-family: 'Segoe UI', sans-serif;
            }
        }
        button {
            padding: 0.5rem 1rem;
            background-color: #222260;
            color: white;
            border: none;
            border-radius: 5px;
            cursor: pointer;
            font-family: 'Segoe UI', sans-serif;
        }
    }
`;

export default Settings;
